import React, { useRef, useState } from "react"
import Layout from "../components/Layout"
import { graphql, useStaticQuery } from "gatsby"
import styles from "./blog.module.scss"

const BlogPage = () => {
  const posts = useStaticQuery(graphql`
    query {
      allMarkdownRemark {
        edges {
          node {
            frontmatter {
              title
              date
              tags
            }
          }
        }
      }
    }
  `)

  const searchInput = useRef(null)
  const clearIcon = useRef(null)
  const [isVisible, setIsVisible] = useState(false)

  const handleClearClick = () => {
    searchInput.current.value = ""
  }

  const handleUserInput = e => {
    if (searchInput.current.value) {
      setIsVisible(true)
    } else {
      setIsVisible(false)
    }

    if (e.key === "Enter") {
      console.log("searching: " + searchInput.current.value)
    }
  }

  const changeVisibility = () => {
    if (isVisible) {
      return styles.clearIcon
    } else {
      return styles.invisible
    }
  }

  const chooseClass = tag => {
    let style
    switch (tag) {
      case "cats":
        style = styles.yellow
        break
      case "english":
        style = styles.crimson
        break
      case "science":
        style = styles.green
        break
      default:
        style = styles.tag
        break
    }
    return style
  }

  return (
    <Layout>
      <div className={styles.blogContainer}>
        <div className={styles.blogWrapper}>
          <h1>Blog</h1>
          <ol>
            {posts.allMarkdownRemark.edges.map((post, index) => {
              return (
                <li key={index}>
                  <p>{post.node.frontmatter.title}</p>
                  <p>{post.node.frontmatter.date}</p>
                  <div className={styles.tagContainer}>
                    {post.node.frontmatter.tags.map((tag, index) => {
                      return (
                        <p className={chooseClass(tag)} key={index}>
                          {tag}
                        </p>
                      )
                    })}
                  </div>
                </li>
              )
            })}
          </ol>
        </div>
        <div className={styles.sidebar}>
          <div className={styles.searchWrapper}>
            <img
              className={styles.searchIcon}
              alt="search icon"
              src="data:image/svg+xml;utf8;base64,"
            />
            <input
              className={styles.search}
              ref={searchInput}
              type="text"
              onKeyUp={handleUserInput}
              placeholder="Search"
            />
            <img
              className={changeVisibility()}
              ref={clearIcon}
              onClick={handleClearClick}
              alt="clear icon"
              src="data:image/svg+xml;utf8;base64,"
            />
          </div>
        </div>
      </div>
    </Layout>
  )
}

export default BlogPage
